INSERT INTO public.pitches (id, user_id, title, description, tags, likes_count, comments_count, trending, created_at) VALUES
('c1000000-0000-0000-0000-000000000001'::uuid, 'b1000000-0000-0000-0000-000000000001'::uuid, 'NovaPay: UPI for the Unbanked', 'We are building a payment infrastructure that works without smartphones using USSD technology. Target: 300M unbanked Indians. Already processing Rs10Cr/month in pilot.', ARRAY['fintech','payments','inclusion'], 142, 18, true, now() - interval '25 days'),
('c1000000-0000-0000-0000-000000000002'::uuid, 'b1000000-0000-0000-0000-000000000002'::uuid, 'EduSpark: Vernacular AI Tutor', 'An AI tutor that teaches in 12 Indian languages using voice. We gamify learning for kids in grades 3-8. 15K DAU, 4.8 rating on Play Store.', ARRAY['edtech','AI','vernacular'], 98, 22, true, now() - interval '22 days'),
('c1000000-0000-0000-0000-000000000003'::uuid, 'b1000000-0000-0000-0000-000000000003'::uuid, 'GreenChain: Tokenizing Carbon Credits', 'We make it easy for Indian SMEs to sell carbon credits via a blockchain marketplace. Already onboarded 120 companies, Rs2Cr in transactions.', ARRAY['climate','blockchain','sustainability'], 76, 14, false, now() - interval '20 days'),
('c1000000-0000-0000-0000-000000000004'::uuid, 'b1000000-0000-0000-0000-000000000004'::uuid, 'MediAI: X-Ray Diagnostics via WhatsApp', 'Doctors in tier-2 and tier-3 cities send X-rays on WhatsApp and get AI-based analysis in 30 seconds. FDA approval in progress. 2M+ scans processed.', ARRAY['healthtech','AI','diagnostics'], 215, 31, true, now() - interval '18 days'),
('c1000000-0000-0000-0000-000000000005'::uuid, 'b1000000-0000-0000-0000-000000000005'::uuid, 'AgriSense: IoT for Small Farmers', 'Solar-powered soil sensors at Rs499 that tell farmers exactly when and how much to water. Yield improvement of 40% validated across 500 farms.', ARRAY['agritech','IoT','sustainability'], 89, 16, false, now() - interval '16 days'),
('c1000000-0000-0000-0000-000000000006'::uuid, 'b1000000-0000-0000-0000-000000000006'::uuid, 'StyleLoop: Rent, Swap, Repeat', 'India first circular fashion app. 80K users renting and swapping clothes. Saved 2M liters of water. Growing 25% MoM.', ARRAY['fashion','sustainability','marketplace'], 134, 27, true, now() - interval '14 days'),
('c1000000-0000-0000-0000-000000000007'::uuid, 'b1000000-0000-0000-0000-000000000007'::uuid, 'LogiZen: AI Routes for Kirana Deliveries', 'Our AI cuts delivery costs by 35% for D2C brands and kirana networks. Processing 50K deliveries/day. Raised $2M from Accel.', ARRAY['logistics','AI','D2C'], 167, 23, true, now() - interval '12 days'),
('c1000000-0000-0000-0000-000000000008'::uuid, 'b1000000-0000-0000-0000-000000000008'::uuid, 'MindBridge: Mental Health in Your Language', 'Connect with certified therapists in Hindi, Tamil, Bengali and 9 more languages. Subscription at Rs299/month. 8K active subscribers.', ARRAY['mentalhealth','vernacular','wellness'], 201, 35, true, now() - interval '10 days'),
('c1000000-0000-0000-0000-000000000009'::uuid, 'b1000000-0000-0000-0000-000000000009'::uuid, 'CryptoKart: SIP for Crypto', 'We let first-time investors do SIPs in crypto starting Rs100/week. Simple UI, no jargon. 50K users, Rs15Cr AUM.', ARRAY['fintech','crypto','investing'], 112, 19, false, now() - interval '9 days'),
('c1000000-0000-0000-0000-000000000010'::uuid, 'b1000000-0000-0000-0000-000000000010'::uuid, 'TalentFlow: Blind Hiring with AI', 'Remove bias from hiring by anonymising resumes. AI matches on skills only. 200+ companies, 30% improvement in diversity metrics.', ARRAY['HR','AI','DEI'], 88, 12, false, now() - interval '8 days'),
('c1000000-0000-0000-0000-000000000011'::uuid, 'b1000000-0000-0000-0000-000000000011'::uuid, 'SpaceApps: Drone Inspections for Bridges', 'Our drones + CV pipeline cuts infrastructure inspection costs by 60% and is 5x safer. Won NITI Aayog pitch competition 2024.', ARRAY['deeptech','drones','infrastructure'], 145, 21, true, now() - interval '7 days'),
('c1000000-0000-0000-0000-000000000012'::uuid, 'b1000000-0000-0000-0000-000000000012'::uuid, 'FoodWeb: Farm-to-Table Supply Chain', 'We eliminate middlemen so farmers earn 2x and restaurants pay 20% less. Serving 500+ restaurants in Bangalore. $500K ARR.', ARRAY['foodtech','supplychain','agritech'], 93, 17, false, now() - interval '6 days'),
('c1000000-0000-0000-0000-000000000013'::uuid, 'b1000000-0000-0000-0000-000000000013'::uuid, 'BuildStack: No-Code for Enterprise', 'Enterprises build internal apps 10x faster with our drag-and-drop builder. SOC 2 certified. 200 companies, $1.2M ARR.', ARRAY['nocode','SaaS','enterprise'], 119, 24, false, now() - interval '5 days'),
('c1000000-0000-0000-0000-000000000014'::uuid, 'b1000000-0000-0000-0000-000000000014'::uuid, 'CareNest: Trusted Elder Care at Home', 'Marketplace for verified caregivers for seniors. Background checked, trained, insured. 5K families served, 4.9 NPS.', ARRAY['eldercare','marketplace','healthtech'], 176, 29, true, now() - interval '3 days'),
('c1000000-0000-0000-0000-000000000015'::uuid, 'b1000000-0000-0000-0000-000000000015'::uuid, 'EV-Link: Charge Your Car at Home', 'We install and manage EV chargers in apartment complexes. Revenue share model, zero capex for RWAs. 120 societies, 3K charging sessions/day.', ARRAY['EVtech','sustainability','infrastructure'], 203, 33, true, now() - interval '1 day')
